package com.erban.main.service.room;

import com.erban.main.model.FaceJson;
import com.erban.main.model.FaceJsonExample;
import com.erban.main.mybatismapper.FaceJsonMapper;
import com.erban.main.service.AppVersionService;
import com.erban.main.service.base.BaseService;
import com.xchat.common.redis.RedisKey;
import com.xchat.common.utils.BlankUtil;
import org.springframework.beans.factory.annotation.Autowired;
import org.springframework.stereotype.Service;

import javax.servlet.http.HttpServletRequest;
import java.util.List;

@Service
public class FaceJsonService extends BaseService {

    @Autowired
    private FaceJsonMapper faceJsonMapper;
    @Autowired
    private AppVersionService appVersionService;

    /**
     * 获取当前有效的表情JSON信息
     *
     * @return
     */
    public FaceJson getValidFaceJson(String os, String appVersion, HttpServletRequest request) {
        String json = jedisService.get(RedisKey.face_json.getKey());
        if (!BlankUtil.isBlank(json)) {
            FaceJson faceJson = gson.fromJson(json, FaceJson.class);
            // IOS新版本在审核期内的首页数据要做特殊处理
            if ("ios".equalsIgnoreCase(os) && appVersionService.checkIsAuditingVersion(appVersion, request)) {
                faceJson.setJson("");
            }
            return faceJson;
        } else {
            FaceJson faceJson = getFaceJsonFromDB();
            jedisService.set(RedisKey.face_json.getKey(), gson.toJson(faceJson));
            // IOS新版本在审核期内的首页数据要做特殊处理
            if ("ios".equalsIgnoreCase(os) && appVersionService.checkIsAuditingVersion(appVersion, request)) {
                faceJson.setJson("");
            }
            return faceJson;
        }
    }

    public FaceJson getFaceJsonFromDB() {
        FaceJsonExample example = new FaceJsonExample();
        example.createCriteria().andStatusEqualTo((byte)1);
        List<FaceJson> list = faceJsonMapper.selectByExampleWithBLOBs(example);
        if (BlankUtil.isBlank(list)) {
            return null;
        }
        return list.get(0);
    }
}
