mapper.select:
select

from comment join users on comment.uid=users.uid


    AND comment.topicid=#{query.topicid}


    AND comment.state=#{query.state}


    AND comment.comment like concat('%',#{query.comment},'%')



   #{page.bottom},#{page.pageSize}

select

from comment join users on comment.uid=users.uid


    AND comment.topicid=#{query.topicid}


    AND comment.state=#{query.state}


    AND comment.comment like concat('%',#{query.comment},'%')



  order by comment.create_time asc


   #{page.bottom},#{page.pageSize}

select count(1)
from comment join users on comment.uid=users.uid


    AND comment.topicid=#{query.topicid}


    AND comment.state=#{query.state}


    AND comment.comment like concat('%',#{query.comment},'%')

select

from comment join users on comment.uid=users.uid
where comment.id=#{id}
