mapper.select:
select

from
    guild_daily_turnover_report



     and member_id = #{memberId}


      and member_id is null




      and hall_id = #{hallId}


      and hall_id is null



    and guild_id=#{guildId}


    AND DATE_FORMAT(report_date, '%Y-%m-%d') = DATE_FORMAT(#{date,jdbcType=TIMESTAMP},'%Y-%m-%d')

select sum(gold) from guild_daily_turnover_report
and member_id = #{memberId}
and member_id is null
and hall_id = #{hallId}
and hall_id is null
and guild_id = #{guildId}
and guild_id is null
AND DATE_FORMAT(report_date, '%Y-%m-%d') >= DATE_FORMAT(#{startTime},'%Y-%m-%d')
AND DATE_FORMAT(report_date, '%Y-%m-%d') <= DATE_FORMAT(#{endTime},'%Y-%m-%d')
