mapper.select:
SELECT version_id AS versionId, os AS os, version AS version, platform AS platform, status AS status, version_desc AS versionDesc, create_time AS createTime, publish_time AS publishTime, appid AS appid, download_url AS downloadUrl FROM app_version
AND version = #{version}
AND os = #{os}
AND appid = #{appId}
order by version_id desc limit 1
